import { Component } from '@angular/core';

@Component({
  selector: 'app-slider',
  templateUrl: './slider.component.html',
  styleUrls: ['./slider.component.css']
})

export class SliderComponent {
  public currentCount = 0;

  public incrementCounter() {
    this.currentCount++;
  }
}
