import { Component } from '@angular/core';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
export class CounterComponent {
  public currentCount:number = 0;
  public step:number = 5;

  public incrementCounter(stepUp:number) {
    this.currentCount+=stepUp;
  }

  public decrementCounter(stepDown:number) {
    this.currentCount-=stepDown;
  }

  public setStep(newStep:any) {
    this.step = parseInt(newStep.target.value, 10);
  }
}
<h1>Counter</h1>

<p>This is a simple example of an Angular component.</p>

<p aria-live="polite">Current count: <strong>{{ currentCount }}</strong></p>

<button class="btn btn-primary" (click)="incrementCounter(step)">Increment</button>
<button class="btn btn-dark" (click)="decrementCounter(step)">Decrement</button>
<br class="clr">

<input type="range" min="1" max="9" value="{{ step }}" class="slider col-sm-8 col-md-5" (change)="setStep($event)">
